mapper.insert:
insert into bibpaper(category, title, authors, abstract, descript, hash_tag,
                      hyperlink, bibtex_source, bibitem_source, remark
                    ) values(#{category}, #{title}, #{authors}, #{abstract},
                      #{descript}, #{hash_tag}, #{hyperlink}, #{bibtex_source},
                      #{bibitem_source}, #{remark}
                    )
